class DSU {
public:
    explicit DSU(size_t n):al(n) {
    }
    void merge(int x, int y, int i) {
        al[x].insert(y);
        al[y].insert(x);
        w[pii(x,y)] = w[pii(y,x)] = i;
    }
    void dfs(int v, vector<bool>& used, vi& prev) {
        if (used[v])
            return;
        used[v] = 1;
        for (int v2: al[v]) {
            if (!used[v2]) {
                prev[v2] = v;
                dfs(v2, used, prev);
            }
        }
    }
    bool connected(int x, int y) {
        vi prev(al.size() , -1);
        vector<bool> used(al.size(), 0);
        dfs(x, used, prev);
        if (prev[y] == -1)
            return 0;
        int mi = INF;
        int A = -1;
        int B = -1;
        while(prev[y] != -1) {
            int a = prev[y];
            int b = y;
            if (mi > w[pii(a,b)]) {
                mi = w[pii(a,b)];
                A = a;
                B = b;
            }
            assert(mi > 0);
            y = prev.at(y);
        }
        al[A].erase(B);
        al[B].erase(A);
        return 1;
    }
private:
    vector<set<int>> al;
    map<pii, int> w;
};

class CandyCupRunningCompetition{
public:
    int findMaximum(int N, vector <int> A, vector <int> B) {
        vector<int> p3(1,1);
        while (p3.size() < N) {
            p3.push_back((3LL*p3.back()) % 1000000007);
        }
        DSU dsu(N);
        int res = 0;
        for (int i = 0; i < A.size(); ++i) {
            int x = A[A.size() - i - 1];
            int y = B[A.size() - i - 1];
            dsu.merge(x,y, A.size() - i);
            if (dsu.connected(0,N-1)) {
                assert(!dsu.connected(0,N-1));
                res = (res + p3[A.size() - 1 - i]) % 1000000007;
            }
        }
        return res;
    }
};

